import React from 'react';
import ReactJson from 'react-json-view'
import JSONTree from 'react-json-tree'


class Dataspec extends React.Component{

    fileReader;
    constructor(props) {
        super(props);
        this.state = {buttonDisplay:""};
      }
     handleFileRead = (e) => {
        const content = this.fileReader.result;
        let input
        try{
            input = JSON.parse(content)
            this.setState({ data:input, buttonDisplay:"true" }); 
        }
        catch{
            this.setState({ data:input, buttonDisplay:"" }); 
            alert("JSON validation error!")
        }
      };

    onFileChange = event => { 

        // Read the file
        this.fileReader = new FileReader();
        this.fileReader.onloadend = this.handleFileRead;
        this.fileReader.readAsText(event.target.files[0]);       
      }; 



    render(){
        return(
            <>
            <div className="w3-half">
                <h3>Stage 0: Data Specification </h3>
                <div className="w3-container w3-padding-32 " style={{margin:"32px 0"}}>
                    <h4 className="w3-center">Input your data specification</h4>
                    <input type="file" className="w3-button w3-indigo w3-margin-top w3-center" onChange={this.onFileChange}/>
                    <div className="w3-margin-top" style={{"maxHeight":"800px","overflowY":"scroll"}}>
                        {/* <ReactJson className="w3-margin-top" src={this.state.data} theme="monokai" validationMessage="Validation Error" displayDataTypes={false}	 />            */}
                        <JSONTree className="w3-margin-top" theme="monokai" invertTheme={true} data={this.state.data}/>
                    </div>
                    <button type="button" disabled={!this.state.buttonDisplay} className="w3-button w3-indigo w3-margin-top w3-right" onClick = {this.props.next} >Proceed</button>
                </div>
             </div>
             <div className="w3-half">
            </div>
            </>
        )
    }
}

export default Dataspec;
