import React from 'react';

class Encoding  extends React.Component{

    constructor(props) {
        super(props);
        this.state = {task1:false, task2: false, task3: false, task4:false};

         this.changeStatus = this.changeStatus.bind(this)

      }

      changeStatus = (val) =>{
          if(val=="task1"){
          this.setState({"task1":!this.state.task1})}
          if(val=="task2"){
            this.setState({"task2":!this.state.task2})}
      }
    render(){
    return(
        <>
            <div className="w3-row-padding">
            <div class="w3-col s9">
            <h3> Enconding Selection</h3>
            <div class="w3-border">
            <div class="w3-bar w3-theme">
            <button class="w3-bar-item w3-button testbtn w3-padding-16" onclick="openCity(event,'London')">London</button>
            <button class="w3-bar-item w3-button testbtn w3-padding-16" onclick="openCity(event,'Paris')">Paris</button>
            <button class="w3-bar-item w3-button testbtn w3-padding-16" onclick="openCity(event,'Tokyo')">Tokyo</button>
            </div>

            <div id="London" class="w3-container city w3-animate-opacity">
            <h2>London</h2>
            <p>London is the capital city of England.</p>
            <p>It is the most populous city in the United Kingdom, with a metropolitan area of over 13 million inhabitants.</p>
            </div>

            <div id="Paris" class="w3-container city w3-animate-opacity">
            <h2>Paris</h2>
            <p>Paris is the capital of France.</p> 
            <p>The Paris area is one of the largest population centers in Europe, with more than 12 million inhabitants.</p>
            </div>

            <div id="Tokyo" class="w3-container city w3-animate-opacity">
            <h2>Tokyo</h2>
            <p>Tokyo is the capital of Japan.</p>
            <p>It is the center of the Greater Tokyo Area, and the most populous metropolitan area in the world.</p>
            </div>
            </div>

            <div class="w3-row-padding w3-padding-16">
                <TaskInput status={this.state.task1} name="task1" changeStatus={this.changeStatus}/>
                <TaskInput status={this.state.task2} name="task2" changeStatus={this.changeStatus}/>
                <TaskInput/>
                <TaskInput/>
            </div>
            </div>
            <div class="w3-col s3">
            </div>
            </div>             
        </>
    )
    }
}

function TaskInput(props){

   function handleChange () {
    props.changeStatus(props.name)           
    }    

    return(
        <div class="w3-col s3 w3-margin-bottom">
        <div class="w3-container w3-white" style = {props.status ? {"border-style":"solid", "border-color":"#80b1d3"}:{}} >
            <h3>Identify</h3>
            <img src={ require('../assets/test.png') } alt="Norway" style={{"width":"100%"}}/>
            <p>Few words to explain the task.Few words to explain the task.</p>
            <h6 class="w3-opacity">Examples: Functional Annotation, Find the Epigenetic Signal Value</h6>
            <button onClick={handleChange} class="w3-button w3-block w3-indigo w3-margin-bottom">Select</button>
        </div>
        </div>
    )
}


export default Encoding;
