import React from 'react';

class Dataspec extends React.Component{

    onFileChange = event => { 

        // Update the state 
        this.setState({ selectedFile: event.target.files[0] }); 

      }; 

    render(){
        return(
            <>
            <div className="w3-container w3-padding-32 w3-black w3-opacity w3-card w3-hover-opacity-off" style={{margin:"32px 0"}}>
            <h2 className="w3-center">Input your data specification</h2>
            <input type="file" className="w3-button w3-red w3-margin-top w3-center"/>

            </div>
            </>
        )
    }
}

export default Dataspec;
