import React from 'react';
import ReactJson from 'react-json-view'

class Dataspec extends React.Component{

    fileReader;
    constructor(props) {
        super(props);
        this.state = {};
      }
     handleFileRead = (e) => {
        const content = this.fileReader.result;
        let input
        try{input = JSON.parse(content)}
        catch{
            alert("JSON validation error!")
        }
        // … do something with the 'content' …
        this.setState({ data:input }); 
      };

    onFileChange = event => { 

        // Read the file
        this.fileReader = new FileReader();
        this.fileReader.onloadend = this.handleFileRead;
        this.fileReader.readAsText(event.target.files[0]);       
      }; 

    render(){
        return(
            <>
            <div className="w3-container w3-padding-32 w3-card" style={{margin:"32px 0"}}>
            <h2 className="w3-center">Input your data specification</h2>
            <input type="file" className="w3-button w3-red w3-margin-top w3-center" onChange={this.onFileChange}/>
            <div className="w3-margin-top" style={{"maxHeight":"200px","overflowY":"scroll"}}>
            <ReactJson className="w3-margin-top" src={this.state.data} theme="shapeshifter:inverted" validationMessage="Validation Error" displayDataTypes={false}	 />           
             </div>

             </div>
            </>
        )
    }
}

export default Dataspec;
